script:
 function calculate_total_working_hours(frm) {
    if(frm.doc.timesheet_detail.length){
        let working_hrs = frm.get_sum('timesheet_detail','hours')
        frm.set_value('total_working_hours', working_hrs)
    }else{
        frm.set_value('total_working_hours', 0)
    }
}
function calculateHours(fromTime, toTime) {
    if (!fromTime || !toTime) {
        console.error("Both From Time and To Time must be provided.");
        return 0;
    }

    const fromTimeMoment = moment(fromTime);
    const toTimeMoment = moment(toTime);

    if (toTimeMoment.isBefore(fromTimeMoment)) {
        console.error("To Time cannot be earlier than From Time.");
        return 0;
    }

    const duration = moment.duration(toTimeMoment.diff(fromTimeMoment));
    return duration.asHours(); // Returns the difference in hours
}
frappe.ui.form.on('Daily Worklog', {
    refresh(frm) {

        let today = new Date();
        let min_date = new Date();
        min_date.setFullYear(today.getFullYear() - 18);
        frm.set_df_property('date', 'max_date', frappe.datetime.str_to_obj(today));
        // Update the datepicker with the new constraints
        $(frm.fields_dict['date'].$input).datepicker({
            maxDate: today,
            // minDate: min_date,
            yearRange: '-100:+0'
        });

    },
    after_save(frm){
         frm.doc.timesheet_detail.forEach((detail) => {
            if (detail.from_time && detail.to_time) {
                const hours = calculateHours(detail.from_time, detail.to_time);
                frappe.model.set_value(detail.doctype, detail.name, 'hours', hours);
            }
        });
    }
});

frappe.ui.form.on('Timesheet Details Child', {
   hours(frm,cdn,cdt){
       calculate_total_working_hours(frm)
   }
});
